##########################################################
####                 COMPUTING POWER                  ####
##########################################################

power <- function(var=list(),dist,k,n1){
  power_results<-data.frame(matrix(0,16,3,dimnames=list(1:16,c("F-test","Welch","B-F"))))
  
  for (s in 1:16){
    power_results[s,1] = round(sum(var[[s]][,1]<.05)/length(var[[s]][,1]),3)
    power_results[s,2] = round(sum(var[[s]][,2]<.05)/length(var[[s]][,2]),3)
    power_results[s,3] = round(sum(var[[s]][,3]<.05)/length(var[[s]][,3]),3)
  }
  
  setwd(dir="C:/Users/Marie/Dropbox/ANOVA's Welch/Outputs of simulations/statistics_power and type 1 error rate") # replace "???" by the desired destination file
  
  write.table(power_results,str_c("power for ",dist," distribution with k=",k," and base n =",n1,".txt"),sep=";",dec=",")
}

 
