##########################################################
####                 COMPUTING ALPHA                  ####
##########################################################

alpha <- function(var=list(),dist,k){
  alpha_results<-data.frame(matrix(0,12,3,dimnames=list(1:12,c("F-test","Welch","B-F"))))
  
  for (s in 1:80){
    alpha_results[s,1] = round(sum(var[[s]][,1]<.05)/length(var[[s]][,1]),3)
    alpha_results[s,2] = round(sum(var[[s]][,2]<.05)/length(var[[s]][,2]),3)
    alpha_results[s,3] = round(sum(var[[s]][,3]<.05)/length(var[[s]][,3]),3)
  }
  
  setwd(dir="C:/Users/Marie/Dropbox/ANOVA's Welch/Outputs of simulations") # replace "???" by desired destination file
  write.table(alpha_results,str_c("alpha for ",dist," distribution with k=",k,".txt"),sep=";",dec=",")
}



